import React, { useState } from 'react'
import { Button, ButtonGroup } from '@mui/material';
import styles from "./BetList.module.css";

interface Props {
    id : number;
    overPct : number,
    underPct : number
}

const overButtonStyle = {
    width: "100px",
    color: "green"
}

const BetButtonGroup: React.FC<Props> = (props) => {
    const [selected, setSelected] = useState<boolean>(false);
    if (selected === true) {
        return (
            <div className={styles.betButtonGroup}>
                <Button className={styles.overBetButton} onClick={() => setSelected(false)}>{props.overPct}%</Button>
                <Button className={styles.underBetButton} onClick={() => setSelected(false)}>{props.underPct}%</Button>
            </div>
        );
    } else {
        return (
            <div className={styles.betButtonGroup}>
                <Button variant="outlined" className={styles.overBetButton} onClick={() => setSelected(true)}>Over</Button>
                <Button className={styles.underBetButton} onClick={() => setSelected(true)}>Under</Button>
            </div>
        );
    }

}

export default BetButtonGroup;
