import React, { useState } from 'react'
import { Button, ButtonGroup } from '@mui/material';
import styles from "./BetList.module.css";

interface Props {
    id : number;
    overPct : number,
    underPct : number
}

interface Dictionary<T> {
    [Key: string]: T;
}

const overButtonStyle = {
    width: "100px",
    color: "green",
    borderColor: "green",
    marginRight: "3px"
}

const underButtonStyle = {
    width: "100px",
    color: "red",
    borderColor: "red"
}

const buttonSelect = (id : number, style : Dictionary<string>, setSelected : Function) => {
    if (id !== 0) {
        style['background'] = "linear-gradient(to right, #efe3af 75%,#ffffff 75%)";
    }
    setSelected(id);
}

const BetButtonGroup: React.FC<Props> = (props) => {
    const [selected, setSelected] = useState<number>(0);
    let overStyle : Dictionary<string> = {...overButtonStyle}
    let underStyle : Dictionary<string> = {...underButtonStyle}

    if (selected === 1) {
        overStyle['background'] = "linear-gradient(to right, #efe3af 75%,#ffffff 75%)";
    } else if (selected === 2) {
        underStyle['background'] = "linear-gradient(to right, #efe3af 75%,#ffffff 75%)";
    }

    if (selected !== 0) {
        return (
            <div className={styles.betButtonGroup}>
                <Button variant="outlined" sx={overButtonStyle} onClick={() => setSelected(0)}>{props.overPct}%</Button>
                <Button variant="outlined" sx={underButtonStyle} onClick={() => setSelected(0)}>{props.underPct}%</Button>
            </div>
        );
    } else {
        return (
            <div className={styles.betButtonGroup}>
                <Button variant="outlined" sx={overButtonStyle} onClick={() => setSelected(1)}>Over</Button>
                <Button variant="outlined" sx={underButtonStyle} onClick={() => setSelected(2)}>Under</Button>
            </div>
        );
    }

}

export default BetButtonGroup;
