import React, { useState } from 'react'
import { Button, ButtonGroup } from '@mui/material';
import styles from "./PropBetListItem.module.css";

interface Props {
    id : number;
    overPct : number,
    underPct : number
}

const BetButtonGroup: React.FC<Props> = (props) => {
    const [selected, setSelected] = useState<boolean>(false);
    if (selected === true) {
        return (
            <ButtonGroup>
                <Button className={styles.betButton} onClick={() => setSelected(false)}>{props.overPct}%</Button>
                <Button className={styles.betButton} onClick={() => setSelected(false)}>{props.underPct}%</Button>
            </ButtonGroup>
        );
    } else {
        return (
            <ButtonGroup>
                <Button className={styles.betButton} onClick={() => setSelected(true)}>Over</Button>
                <Button className={styles.betButton} onClick={() => setSelected(true)}>Under</Button>
            </ButtonGroup>
        );
    }

}

export default BetButtonGroup;
